import './UploadView.scss';
import React, { useCallback } from 'react';
import { useDropzone } from 'react-dropzone'

const ImagesUploader = props => {
    const { uploads, setUploads } = props;


    const handleTitleUpdate = (value, idx) => {
        const newState =  [
        ...uploads.slice(0, idx),
        Object.assign({}, uploads, {title: value}),
        ...uploads.slice(idx + 1),
        ]
        setUploads(newState)
    }

    const handleDescriptionUpdate = (value, idx) => {

    }

  const uploadRow = (upload, idx) => {
    return (
    <div className="sectionBody">
        <div className="imageContainer">
            <img className="uploadImage"src = {upload.preview}/>
        </div>
        <div className="uploadDescription">
            <input placeholder="title" className="imageInput" 
                value={upload.headline} onChange={e => handleTitleUpdate(e.target.value, idx)}/>
            <input placeholder="description" className="imageInput" 
                value={upload.description} onChange={e => handleDescriptionUpdate(e.target.value, idx)}/>
        </div>
    </div>
    )
}   


const onDrop = useCallback(files => {

    const filesWithPreview = files.map(file => Object.assign(file, {
        preview: URL.createObjectURL(file)
    }));

    const filesForState = filesWithPreview.map(file => ({preview: file.preview, url: file}))
    setUploads(uploads.concat(filesForState))

    // debugger;

    // setLeadImage(Object.assign({}, leadImage, {preview: image.preview, url: image}))
  }, [uploads])

  const { getRootProps, getInputProps } = useDropzone({onDrop})


  return (
    <div className="imageUploadContainer">
        <div {...getRootProps({className: 'dropzone'})}>
                <input {...getInputProps()} />
                <p>Drag 'n' drop some files here, or click to select files</p>
            </div>
        <div className="sectionHeader">Additional Images</div>
        {uploads.map((upload, idx) => uploadRow(upload, idx))}
    </div>
  )
}

export default ImagesUploader
