// import logo from './logo.svg';
import './App.css';
import React, { useState, useEffect } from 'react';

function App() {
  const [featuredImage, setFeaturedImage] = useState({url: "", description: ""});
  const [apiImages, setApiImages] = useState([])

const handleScroll = () => {
    console.log(apiImages)
    const percentageOfPageScrolledTo = window.scrollY / (window.innerHeight * (apiImages.length));
    const desiredIndex = Math.round(apiImages.length * percentageOfPageScrolledTo);
    if (window.scrollY < window.innerHeight / images.length) {
      setFeaturedImage(apiImages[0])
    } else if (desiredIndex !== images.indexOf(featuredImage)) {
      setFeaturedImage(images[desiredIndex]) 
    }
  }

  useEffect(()=> {
    document.addEventListener('scroll', handleScroll)
    fetch('http://localhost:3005/artwork')
        .then(result => result.json())
        .then(json => {

          setApiImages(json.images.images)
          setFeaturedImage(json.info)
        })
        .catch((err) => {
          console.log(err);   
        });
    return () => document.removeEventListener('scroll', handleScroll) 
  }, [])

  const scrollerStyles = {
    height: `${window.innerHeight * (apiImages.length)}px`
  }

  return (
    <div className="scroller" id="scroller">
      <img className="featuredImage" src={featuredImage["url"] || ''}></img>
      <div className="scrollingPanel" style={scrollerStyles}>
        <div className="imageDescription">{featuredImage["description"] || ''}</div>
      </div>
    </div>
  );
}

export default App
