// import logo from './logo.svg';
import './App.css';
import React, { useState, useEffect } from 'react';

function App() {
  const images = [
    {description: "picOne"},
    {
      url: "https://4.img-dpreview.com/files/p/E~TS590x0~articles/3925134721/0266554465.jpeg",
      description: "picTwo"
    },
    {
      url: "https://fujifilm-x.com/wp-content/uploads/2019/08/xc16-50mmf35-56-ois-2_sample-images03.jpg",
      description: "picThree"
    },
    {
      url: "https://www.cameraegg.org/wp-content/uploads/2015/06/canon-powershot-g3-x-sample-images-1-900x600.jpg",
      description: "picFour"
    }
  ]

  const [featuredImage, setFeaturedImage] = useState(images[0]);

  const handleScroll = () => {
    const percentageOfPageScrolledTo = window.scrollY / (window.innerHeight * (images.length));
    const desiredIndex = Math.round(images.length * percentageOfPageScrolledTo);
    if (window.scrollY < window.innerHeight / images.length) {
      setFeaturedImage(images[0])
    } else if (desiredIndex !== images.indexOf(featuredImage)) {
      setFeaturedImage(images[desiredIndex]) 
    }
  }

  useEffect(()=> {
    document.addEventListener('scroll', handleScroll)
    return () => document.removeEventListener('scroll', handleScroll) 
  }, [])



  const scrollerStyles = {
    height: `${window.innerHeight * (images.length)}px`
  }

  return (
    <div className="scroller" id="scroller" style={scrollerStyles}>
      <img className="featuredImage" src={featuredImage["url"]}></img>
      <div clasName="scrollingPanel">
        <div className="imageDescription">{featuredImage["description"]}</div>
      </div>
    </div>
  );
}

export default App
