import './UploadView.scss';
import React, { useCallback } from 'react';
import { useDropzone } from 'react-dropzone'

const ImagesUploader = props => {
    const { uploads, setUploads } = props;

  const uploadRow = upload => {
    return (
    <div className="sectionBody">
        <div className="imageContainer">
            <img className="uploadImage"src = {upload.url}/>
        </div>
        <div className="uploadDescription">
            <input placeholder="title" className="imageInput" value={upload.headline}/>
            <input placeholder="description" className="imageInput" value={upload.description}/>
        </div>
    </div>
    )
}   

const onDrop = useCallback(file => {

    debugger;

    // const uploadedFile = file[0]

    // const image = Object.assign({}, uploadedFile, {
    //     preview: URL.createObjectURL(uploadedFile)
    // })

    // setLeadImage(Object.assign({}, leadImage, {preview: image.preview, url: image}))
  }, [uploads])

  const { getRootProps, getInputProps } = useDropzone({onDrop})


  return (
    <div className="imageUploadContainer">
        <div {...getRootProps({className: 'dropzone'})}>
                <input {...getInputProps()} />
                <p>Drag 'n' drop some files here, or click to select files</p>
            </div>
        <div className="sectionHeader">Additional Images</div>
        {uploads.map(upload => uploadRow(upload))}
    </div>
  )
}

export default ImagesUploader
