import './App.scss';
import React from 'react';

const Header = props => {
  const { artworkInfo, displaySplashScreen } = props

  const headerStyles = {
      background: displaySplashScreen ? "linearGradient(#efe6e1 50%, #1c1b1b)" : "#1c1b1b",
      boxShadow: displaySplashScreen ? '0 2px 5px -2px #e0dad6' : 'none',
      color: displaySplashScreen ? "#1c1b1b" : "#efe6e1"
  }

  return (
    <div className="header" style={headerStyles}>{artworkInfo.description}</div>
  )
}

export default Header
