// import logo from './logo.svg';
import './App.css';
import React, { useState, useEffect } from 'react';
import ScrollerStuff from './ScrollerStuff';

function App() {
  const [featuredImage, setFeaturedImage] = useState(null);
  const [apiImages, setApiImages] = useState([])
  const [artworkInfo, setArtworkInfo] = useState({})
  const [displaySplashScreen, setDisplaySplashScreen] = useState(true)

  const handleScroll = () => {
    const percentageOfPageScrolledTo = window.scrollY / (window.innerHeight * (apiImages.length));
    const desiredIndex = Math.round(apiImages.length * percentageOfPageScrolledTo);
    if (window.scrollY < window.innerHeight / apiImages.length) {
      setDisplaySplashScreen = true 
    } else if (desiredIndex !== apiImages.indexOf(featuredImage)) {
      setDisplaySplashScreen = false
      setFeaturedImage(apiImages[desiredIndex]) 
    }
  }

  useEffect(() => {
    //TO DO api service 
    fetch('http://localhost:3005/artwork')
      .then(result => result.json())
      .then(json => {
        const artworkInfo = json.info[0]
        setApiImages([artworkInfo, ...json.images.images])
        setFeaturedImage(artworkInfo)
        setArtworkInfo(artworkInfo)
      })
      .catch((err) => {
        console.log(err);   
      }); 
  }, [])

  useEffect(()=> {
    document.addEventListener('scroll', handleScroll)
    return () => document.removeEventListener('scroll', handleScroll) 
  }, [apiImages, handleScroll])

  const pageContent = displaySplashScreen ? 
    (<SplashScreen artworkInfo={artworkInfo} />)
     : 
     (
    <div className="scroller" id="scroller">
        <ScrollerStuff apiImages={apiImages} featuredImage={featuredImage} />
      </div>
  )

  return (
    <>
      <Header artworkInfo={artworkInfo} />
      {pageContent}
    </>
  );
}

export default App
