import './UploadView.scss';
import React, { useState, useEffect, useCallback } from 'react';
import { useDropzone } from 'react-dropzone'

const Upload = () => {

    const sampleData = [
        {url: 'http://avrambillig.com/history/images/raft-figure1.jpg',
        headline: 'Water',
        description: "The figures in this cartoon float on an ocean referred to as hard times."},
        {url: 'http://avrambillig.com/history/images/raft-figure7.jpg',
        headline: 'Vanderbilt',
        description: "Cornelius Vanderbilt."},
        {url: 'http://avrambillig.com/history/images/raft-figure7.jpg',
        headline: 'Vanderbilt',
        description: ""}
    ];

    const sampleLead = {
        name: 'The "Boss" Puzzle',
        url: 'http://avrambillig.com/history/images/lead_image.png'
    }

  const [uploads, setUploads] = useState([]);
  const [leadImage, setLeadImage] = useState({url: '', title: ''});

  const onDrop = useCallback(file => {

    const leadImage = file[0]

    const image = Object.assign(leadImage, {
        preview: URL.createObjectURL(leadImage)
    })

    setLeadImage({url: image.preview})

  }, [])
  const { getRootProps, getInputProps } = useDropzone({onDrop})

  const uploadRow = upload => {
      return (
        <div className="sectionBody">
            <div className="imageContainer">
                <img className="uploadImage"src = {upload.url}/>
            </div>
            <div className="uploadDescription">
                <input placeholder="title" className="imageInput" value={upload.headline}/>
                <input placeholder="description" className="imageInput" value={upload.description}/>
            </div>
        </div>
      )
  }

  return (
    <div className="uploadView"> 
        <div className="imageUploadContainer">
            <div className="sectionHeader">Lead Image</div>
            <div {...getRootProps({className: 'dropzone'})}>
                <input {...getInputProps()} />
                <p>Drag 'n' drop some files here, or click to select files</p>
            </div>
            <div className="sectionBody">
                <img className="leadImage" src={leadImage.url}></img>
                <div className="uploadDescription">
                    <input placeholder="lead image description" className="imageInput" value={leadImage.name}/>
                </div>
            </div>
        </div>
        <div className="imageUploadContainer">
            <div className="sectionHeader">Additional Images</div>
            {uploads.map(upload => uploadRow(upload))}
        </div>
    </div>
  )
}

export default Upload
