// import logo from './logo.svg';
import './App.css';
import React from 'react';

const ScrollerStuff = props => {
    const {featuredImage, apiImages} = props

  const scrollerStyles = {
    height: `${window.innerHeight * (apiImages.length)}px`
  }

  const imageDescriptions = apiImages.map(image => {
    return <div className="imageDescription">{image["description"]}</div>
  })

  return (
    <div className="scroller">
      <div className="scrollingPanel" style={scrollerStyles}>
        {imageDescriptions}
      </div>
    </div>
  );
}

export default ScrollerStuff
