  }

  useEffect(()=> {
    //1. Bind the scroll element and add the scroll event, which will be removed when the component is destroyed
    const scrollDom = document.getElementById('scroller') 
    scrollDom.addEventListener('scroll', handleScroll)
    return () => scrollDom.removeEventListener('scroll', handleScroll); 
}, [handleScroll])

